import React from "react";
import Logs from "./logs";
import "./styles/selection.css";

const Play = ({
  action,
  setAction,
  board,
  setBoard,
  setPlay,
  logs,
  setLogs,
  loading,
}) => {
  const surrender = () => {
    Object.keys(board).forEach((box) => {
      board[box] = "";
    });
    setBoard({ ...board });
    setPlay(false);
  };

  const handleCancel = () => {
    Object.keys(board).forEach((pos) => {
      board[pos].style = "";
      board[pos].legal = false;
      board[pos].playing = false;
    });
    setBoard({ ...board });
  };
  return (
    <div className="side-bar">
      <h2>Seleccione una acci√≥n</h2>
      {!loading ? (
        <div className="ship-buttons">
          <button
            className="play-button"
            onClick={() => setAction("move")}
            disabled={action === "fire"}
          >
            Mover
          </button>
          <button
            className="play-button"
            onClick={() => setAction("fire")}
            disabled={action === "move"}
          >
            Disparar
          </button>
          <button
            className="ship-button"
            disabled={action === ""}
            onClick={() => {
              handleCancel();
              setAction("");
            }}
          >
            Cancelar
          </button>
        </div>
      ) : (
        <p>loading</p>
      )}

      <Logs logs={logs} setLogs={setLogs} />
      <div className="ship-buttons">
        <button className="ship-button" onClick={surrender}>
          Rendirse
        </button>
      </div>
    </div>
  );
};

export default Play;
