import React, { useState, useEffect } from "react";
import "./styles/box.css";

const Box = ({ pos, selected, setSelected, board, setBoard, action }) => {
  const [active, setActive] = useState(false);
  const letters = {
    A: 1,
    B: 2,
    C: 3,
    D: 4,
    E: 5,
    F: 6,
    G: 7,
    H: 8,
    I: 9,
    J: 10,
  };
  const fireRange = { F: 4, C: 3, D: 2, A: 1 };
  const moveRange = { F: 2, C: 2, D: 3, A: 5 };

  useEffect(() => {
    if (pos === "D5") {
      board[pos] = { value: "F1", legal: false };
    } else {
      board[pos] = { value: "", legal: false };
    }
    setBoard({ ...board });
  }, []);

  useEffect(() => {
    !action && setActive(false);
  }, [action]);

  const handleClick = () => {
    validate();
    if (board[pos] === "" && selected) {
      board[pos] = selected;
      setBoard({ ...board });
      setSelected("");
    } else if (board[pos] !== "" && action) {
      setActive(true);
      if (action === "move") {
        validate();
      }
    }
  };

  const validate = () => {
    Object.keys(board).forEach((pos2) => {
      if (
        pos2.slice(1) === pos.slice(1) &&
        Math.abs(letters[pos2[0]] - letters[pos[0]]) <=
          moveRange[board[pos].value[0]]
      ) {
        console.log(pos2);
      } else if (
        pos[0] === pos2[0] &&
        Math.abs(
          parseInt(pos2.slice(1)) - parseInt(pos.slice(1)) <=
            moveRange[board[pos].value[0]]
        )
      ) {
        console.log(pos2);
      }
    });
  };

  return (
    <div className={active ? "box active" : "box"} onClick={handleClick}>
      <p>{board[pos] && board[pos].value}</p>
    </div>
  );
};

export default Box;
