import React, { useState, useEffect } from "react";
import "./styles/box.css";

const Box = ({
  pos,
  selected,
  setSelected,
  board,
  setBoard,
  action,
  setAction,
  play,
}) => {
  const letters = {
    A: 1,
    B: 2,
    C: 3,
    D: 4,
    E: 5,
    F: 6,
    G: 7,
    H: 8,
    I: 9,
    J: 10,
  };
  const fireRange = { F: 4, C: 3, D: 2, A: 1 };
  const moveRange = { F: 2, C: 2, D: 3, A: 5 };

  useEffect(() => {
    if (play) {
      if (pos === "D5") {
        board[pos] = { ship: "F1", legal: false, style: "", playing: false };
      } else {
        board[pos] = { ship: "", legal: false, style: "", playing: false };
      }
      setBoard({ ...board });
    }
  }, []);

  const handleClick = () => {
    if (board[pos].ship === "" && selected) {
      console.log(selected);
      board[pos].ship = selected;
      setBoard({ ...board });
      setSelected("");
    } else if (board[pos].ship !== "" && action) {
      board[pos].playing = true;
      if (action === "move") {
        validateMove();
      } else {
        validateFire();
      }
    } else if (board[pos].legal) {
      setAction("");
      if (action === "move") {
        moveShip();
      }
    }
  };

  const moveShip = () => {
    Object.keys(board).forEach((pos2) => {
      if (board[pos2].playing) {
        board[pos].ship = board[pos2].ship;
        board[pos2].playing = false;
        board[pos2].ship = "";
      }
      board[pos2].style = "";
      board[pos2].legal = false;
    });
    setBoard({ ...board });
  };

  const validateMove = () => {
    Object.keys(board).forEach((pos2) => {
      if (pos !== pos2) {
        if (
          pos2.slice(1) === pos.slice(1) &&
          Math.abs(letters[pos2[0]] - letters[pos[0]]) <=
            moveRange[board[pos].ship[0]]
        ) {
          board[pos2].legal = true;
          board[pos2].style = "legit";
        } else if (
          pos[0] === pos2[0] &&
          Math.abs(parseInt(pos2.slice(1)) - parseInt(pos.slice(1))) <=
            moveRange[board[pos].ship[0]]
        ) {
          board[pos2].legal = true;
          board[pos2].style = "legit";
        }
      }
    });
    setBoard({ ...board });
  };

  const validateFire = () => {
    Object.keys(board).forEach((pos2) => {
      if (
        pos2.slice(1) === pos.slice(1) &&
        Math.abs(letters[pos2[0]] - letters[pos[0]]) <=
          fireRange[board[pos].ship[0]] &&
        board[pos2].ship === ""
      ) {
        board[pos2].legal = true;
        board[pos2].style = "attack";
      } else if (
        pos[0] === pos2[0] &&
        Math.abs(parseInt(pos2.slice(1)) - parseInt(pos.slice(1))) <=
          fireRange[board[pos].ship[0]] &&
        board[pos2].ship === ""
      ) {
        board[pos2].legal = true;
        board[pos2].style = "attack";
      }
    });
    setBoard({ ...board });
  };

  return (
    <div
      className={`box ${board[pos] && board[pos].playing && "active "} ${
        board[pos] && board[pos].style
      }`}
      onClick={handleClick}
    >
      <p>{board[pos] && board[pos].ship}</p>
    </div>
  );
};

export default Box;
