import React, { useState } from "react";
import "./styles/box.css";

const Box = ({ row, column, selected }) => {
  const [value, setValue] = useState("");
  const handleClick = () => {
    if (!value) {
      setValue(selected);
    }
  };
  return (
    <div className="box" onClick={handleClick}>
      {value}
    </div>
  );
};

export default Box;
