import React, { useState } from "react";
import Board from "./board";
import Selection from "./selection";
import Play from "./play";
import "./styles/game.css";
import { getGameId } from "./api/requests";

const Game = () => {
  const [selected, setSelected] = useState("");
  const [board, setBoard] = useState({});
  const [play, setPlay] = useState(true);
  const [action, setAction] = useState("");
  const [logs, setLogs] = useState([]);
  const [gameId, setGameId] = useState(getGameId().gameId);

  return (
    <div className="frame">
      <h1 className="title">IIC2513 - BattleShip</h1>
      <div className="columns">
        <div className="column is-two-thirds">
          <Board
            selected={selected}
            setSelected={setSelected}
            board={board}
            setBoard={setBoard}
            action={action}
            setAction={setAction}
            play={play}
            logs={logs}
            setLogs={setLogs}
          />
        </div>
        <div className="column">
          {play ? (
            <Play
              action={action}
              setAction={setAction}
              board={board}
              setBoard={setBoard}
              setPlay={setPlay}
              logs={logs}
              setLogs={setLogs}
            />
          ) : (
            <Selection
              selected={selected}
              setSelected={setSelected}
              board={board}
              setBoard={setBoard}
              setPlay={setPlay}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default Game;
