import React, { useState, useEffect } from "react";
import "./styles/box.css";

const Box = ({ pos, selected, setSelected, board, setBoard, action }) => {
  const [active, setActive] = useState(false);

  const fireRange = { F: 4, C: 3, D: 2, A: 1 };
  const moveRange = { F: 2, C: 2, D: 3, A: 5 };
  useEffect(() => {
    if (pos === "5D") {
      board[pos] = "X";
    } else {
      board[pos] = "";
    }
    setBoard({ ...board });
  }, []);

  useEffect(() => {
    !action && setActive(false);
  }, [action]);

  const handleClick = () => {
    console.log(action);
    if (board[pos] === "" && selected) {
      board[pos] = selected;
      setBoard({ ...board });
      setSelected("");
    } else if (board[pos] !== "" && action) {
      setActive(true);
      if (action === "move") {
      }
    }
  };

  return (
    <div className={active ? "box active" : "box"} onClick={handleClick}>
      <p>{board[pos]}</p>
    </div>
  );
};

export default Box;
