import React, { useState, useEffect } from "react";
import "./styles/box.css";

const Box = ({ pos, selected, setSelected, board, setBoard, action }) => {
  const [active, setActive] = useState(false);
  const letters = {
    A: 1,
    B: 2,
    C: 3,
    D: 4,
    E: 5,
    F: 6,
    G: 7,
    H: 8,
    I: 9,
    J: 10,
  };
  const fireRange = { F: 4, C: 3, D: 2, A: 1 };
  const moveRange = { F: 2, C: 2, D: 3, A: 5 };

  useEffect(() => {
    if (pos === "D5") {
      board[pos] = { value: "F1", legal: false, style: "" };
    } else {
      board[pos] = { value: "", legal: false, style: "" };
    }
    setBoard({ ...board });
  }, []);

  useEffect(() => {
    !action && setActive(false);
  }, [action]);

  const handleClick = () => {
    validateMove();
    if (board[pos] === "" && selected) {
      board[pos] = selected;
      setBoard({ ...board });
      setSelected("");
    } else if (board[pos] !== "" && action) {
      setActive(true);
      if (action === "move") {
        validateMove();
      } else {
        validateFire();
      }
    }
  };

  const validateMove = () => {
    Object.keys(board).forEach((pos2) => {
      if (
        pos2.slice(1) === pos.slice(1) &&
        Math.abs(letters[pos2[0]] - letters[pos[0]]) <=
          moveRange[board[pos].value[0]]
      ) {
        board[pos2].legal = true;
      } else if (
        pos[0] === pos2[0] &&
        Math.abs(parseInt(pos2.slice(1)) - parseInt(pos.slice(1))) <=
          moveRange[board[pos].value[0]]
      ) {
        board[pos2].legal = true;
      }
    });
    setBoard({ ...board });
  };

  const validateFire = () => {
    Object.keys(board).forEach((pos2) => {
      if (
        pos2.slice(1) === pos.slice(1) &&
        Math.abs(letters[pos2[0]] - letters[pos[0]]) <=
          fireRange[board[pos].value[0]]
      ) {
        board[pos2].legal = true;
        board[pos2].style = "attack";
      } else if (
        pos[0] === pos2[0] &&
        Math.abs(parseInt(pos2.slice(1)) - parseInt(pos.slice(1))) <=
          fireRange[board[pos].value[0]]
      ) {
        board[pos2].legal = true;
        board[pos2].style = "attack";
      }
    });
    setBoard({ ...board });
  };

  return (
    <div
      className={`box ${active && "active "} ${board[pos] && board[pos].style}`}
      onClick={handleClick}
    >
      <p>{board[pos] && board[pos].value}</p>
    </div>
  );
};

export default Box;
