import React, { useState, useEffect } from "react";
import "./styles/box.css";

const Box = ({ pos, selected, setSelected, board, setBoard, action }) => {
  useEffect(() => {
    board[pos] = "";
    setBoard({ ...board });
  }, []);

  const handleClick = () => {
    if (board[pos] === "" && selected) {
      board[pos] = selected;
      setBoard({ ...board });
      setSelected("");
    } else if (board[pos] !== "" && action) {
      if (action === "move") {
        console.log();
      }
    }
  };
  return (
    <div className="box" onClick={handleClick}>
      <p>{board[pos]}</p>
    </div>
  );
};

export default Box;
