import React, { useState } from "react";
import "./styles/box.css";

const Box = ({ row, column, selected, setSelected }) => {
  const [value, setValue] = useState("");
  const handleClick = () => {
    if (!value && selected) {
      setValue(selected);
      setSelected("");
    }
  };
  return (
    <div className="box" onClick={handleClick}>
      <p>{value}</p>
    </div>
  );
};

export default Box;
