import React, { useState } from "react";
import Logs from "./logs";
import "./styles/selection.css";

const Play = () => {
  const [action, setAction] = useState("");
  return (
    <div className="side-bar">
      <h2>Seleccione una acci√≥n</h2>
      <div className="ship-buttons">
        <button className="ship-button" onClick={() => setAction("move")}>
          Mover
        </button>
        <button className="ship-button" onClick={() => setAction("fire")}>
          Disparar
        </button>
        <button className="ship-button" disabled={action === ""}>
          Cancelar
        </button>
      </div>
      <Logs />
    </div>
  );
};

export default Play;
