import React, { useState } from "react";
import "./styles/logic.css";

const Logic = () => {
  const [frigate, setFrigate] = useState(4);
  const [cruiser, setCruiser] = useState(3);
  const [destructor, setDestructor] = useState(2);
  const [aircraft, setAircraft] = useState(1);

  const handleClick = (e) => {
    console.log(e);
  };

  return (
    <div>
      <h3>Elija una nave y luego donde quiere colocarla</h3>
      <div className="ship-buttons">
        <button className="ship-button" onClick={handleClick}>
          Fragata {frigate}
        </button>
        <button className="ship-button">Crucero {cruiser}</button>
        <button className="ship-button">Destructor {destructor}</button>
        <button className="ship-button">Portaviones {aircraft}</button>
      </div>
    </div>
  );
};

export default Logic;
