import React, { useState, useEffect } from "react";
import "./styles/box.css";

const Box = ({ pos, selected, setSelected, board, setBoard }) => {
  const [value, setValue] = useState("");
  useEffect(() => {
    board[pos] = value;
    setBoard({ ...board });
  }, [setBoard]);
  const handleClick = () => {
    if (!value && selected) {
      setValue(selected);
      setSelected("");
    }
  };
  return (
    <div className="box" onClick={handleClick}>
      <p>{value}</p>
    </div>
  );
};

export default Box;
